// SPDX-License-Identifier: MIT

pragma solidity >=0.8.19 < 0.8.24;

// Interface : interfaces acts like a blueprint for communition between contracts by specifying function signatures(dataTypes) without implementions(declaration).


// Uniswap Example
interface UniswapV2Factory {
    function getPair(address tokenA, address tokenB) external view returns(address pair);
}

interface UniswapV2Pair {
    function getReserves() external view returns(uint112 reserve0, uint112 reserve1, uint32 blackTimestampLast);
}

contract UniswapExample {
    address private factory = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;
    address private dai = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
    address private eth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    function getTokenReserves() external view returns(uint,uint) {
        address pair = UniswapV2Factory(factory).getPair(dai, eth);
        (uint reserve0, uint reserve1, ) = UniswapV2Pair(pair).getReserves();
        return (reserve0, reserve1);
    }

}

